
public class ExampleWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean flag=true;
		while(flag) {
			System.out.println("Hello Testing - One times");
			flag=false;
		}
		
		
		boolean temp=false;
		while(temp) {
			System.out.println("Hello Testing - One times");
			temp=true;
	
		}
	}

}
