
public class SwitchCaseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int menu=3;
	switch(menu) {
	case 1:
		System.out.println("Operation name - Add");
		break;
	case 2:
		System.out.println("Operation name - Substract");
		break;
	case 3:
		System.out.println("Operation name - Multiply");
		break;
	case 4:
		System.out.println("Operation name - Division");
		break;
	default:
			System.out.println("None - Operation");
		break;
	}
		
	}

}
