
public class DataTypeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primitive Data Type
		int a=100;
		char c='A';
		double d=1890.78;
		boolean flag=true;
		float e=90.56f;
		System.out.println("Integer value "+a);
		System.out.println("Character value "+c);
		System.out.println("Double value "+d);
		System.out.println("Boolean value "+flag);
		System.out.println("Floating point value "+e);
		// Non Primitive Data
		String name="Java Language";
		System.out.println("String value"+name);
		Integer z=100;
		System.out.println("Integer Object value "+z);
		Float f=45.67f;
		System.out.println("Float Object value "+f);
		Double g=456789.00;
		System.out.println("Double Object value "+g);
		Boolean flag1=true;
		System.out.println("Boolean Object value "+flag1);
		
	}

}
